import tkinter as tk
from tkinter import *
from tkinter import filedialog
import customtkinter as ctk
import pygame
from PIL import Image
import pywinstyles
from threading import *
import os

#janela e menu de músicas ------------------------
janela = ctk.CTk()
janela.title('AndPlayer')
janela.geometry('720x380') 
janela.resizable(False, False)
ctk.set_appearance_mode ('Light')
pywinstyles.apply_style(janela, "dark")
menu_de_musicas = Menu(janela)

#ativando a extensão player do pygame ------------------
pygame.mixer.init()

#função carregar música ------------------
def abrir_pasta():
   pasta = filedialog.askdirectory()
   if pasta:
        os.chdir(pasta)
        musicas = os.listdir(pasta)
        for musica in musicas:
            if musica.endswith (".mp3"):
                listaDeMusicas.insert(END, musica)       

#função para tocar música ------------------
def tocar():
    musica = listaDeMusicas.get(ACTIVE)
    print (musica [0:-4])
    pygame.mixer.music.load(musica)
    pygame.mixer.music.play(loops=0)

#função para pausar música ------------------
global pausado
pausado = False
def pausar(is_pausado):
    #pausar e despausar
    global pausado
    pausado = is_pausado
    if pausado:
        pygame.mixer.music.unpause()
        pausado = False
    else:    
        pygame.mixer.music.pause()
        pausado = True

#função para próxima música ------------------
def proxima_musica():
    #puxar o numero da tupla do som que está tocando
    proxima = listaDeMusicas.curselection()
    #adicionar um numero no numero da tupla do som que está tocando
    proxima = proxima[0]+1
    #pegar o titulo da música que está na listaDeMusicas
    musica = listaDeMusicas.get(proxima)
    #carregar e tocar o som
    pygame.mixer.music.load(musica)
    pygame.mixer.music.play(loops=0)
    #limpando a barra que aparece na música da listaDeMusicas
    listaDeMusicas.selection_clear(0, END)
    #ativando uma nova barra
    listaDeMusicas.activate(proxima)
    #colocando a nova barra ativada no próximo som
    listaDeMusicas.selection_set(proxima, last=None)

#função para voltar música ------------------
def voltar_musica():
    #puxar o numero da tupla do som que está tocando
    proxima = listaDeMusicas.curselection()
    #adicionar um numero no numero da tupla do som que está tocando
    proxima = proxima[0]-1
    #pegar o titulo da música que está na listaDeMusicas
    musica = listaDeMusicas.get(proxima)
    #carregar e tocar o som
    pygame.mixer.music.load(musica)
    pygame.mixer.music.play(loops=0)
    #limpando a barra que aparece na música da listaDeMusicas
    listaDeMusicas.selection_clear(0, END)
    #ativando uma nova barra
    listaDeMusicas.activate(proxima)
    #colocando a nova barra ativada no próximo som
    listaDeMusicas.selection_set(proxima, last=None)

#função para volume ---------------------
def volume(x):
    pygame.mixer.music.set_volume(int(x)/100)

#criando a lista de músicas ------------------
listaDeMusicas = Listbox(janela, bg= '#1C1C1C', fg='#F5DEB3', width=100, height=19)
listaDeMusicas.pack(padx=10, pady=10)

#criando a base 64 para as imagens -------------------
IMAGEM_TOCAR_64 = r""
IMAGEM_PAUSAR_64 = r""
IMAGEM_PROXIMA_64= r""
IMAGEM_VOLTAR_64 = r""

#importando imagens dos botões e criando variáveis para elas -------------------
imagem_tocar = PhotoImage(data=IMAGEM_TOCAR_64)
imagem_pausar = PhotoImage(data=IMAGEM_PAUSAR_64)
imagem_proxima = PhotoImage(data=IMAGEM_PROXIMA_64) 
imagem_voltar = PhotoImage(data=IMAGEM_VOLTAR_64)

#criando tipo uma div para a parte do volume na janela ---------------------
frame_mestra_volume = ctk.CTkFrame(janela)
frame_mestra_volume.pack(padx=10, pady=10)

#posicionando botões ---------------------
barra_volume = ctk.CTkSlider(frame_mestra_volume, from_=100, to=0, command=volume, orientation= 'horizontal', width=300)
barra_volume.set(100)
barra_volume.pack()

#criando tipo uma div para a parte dos botões na janela ---------------------
botoes = ctk.CTkFrame(janela)
botoes.pack(pady= 10)

#atribuindo as funções desses botões ---------------------
botao_tocar = ctk.CTkButton(botoes, text= 'Play', image= imagem_tocar, command= tocar, fg_color= "#4682B4", text_color= "black")
botao_pausar = ctk.CTkButton(botoes, text= 'Pause/Despause', image= imagem_pausar, command= lambda: pausar(pausado), fg_color= "#4682B4", text_color= "black")
botao_proxima = ctk.CTkButton(botoes, text= 'Next', image= imagem_proxima, command= proxima_musica, fg_color= "#4682B4", text_color= "black")
botao_voltar = ctk.CTkButton(botoes, text= 'Previous', image= imagem_voltar, command= voltar_musica, fg_color= "#4682B4", text_color= "black")

#posicionando esses botões ---------------------
botao_tocar.grid(column=0, row=0, padx=7, pady=10)
botao_pausar.grid(column=1, row=0, padx=7, pady=10)
botao_proxima.grid(column=2, row=0, padx=7, pady=10)
botao_voltar.grid(column=3, row=0, padx=7, pady=10)

#inserindo 'carregar música' e o menu na interface ----------------
carregar_musica_para_menu = Menu(menu_de_musicas, tearoff=0)
menu_de_musicas.add_cascade(label = 'Load', menu= carregar_musica_para_menu)
carregar_musica_para_menu.add_command(label = 'Add Path', command=abrir_pasta)
janela.config(menu=menu_de_musicas)

janela.mainloop()